package estrutura_repeticao;

public class Aula45Contador {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i < 5; i++) {
			System.out.println("Valor de I : "+ i);

		}
		
		for (int i = 4; i >=0; i--) {
			System.out.println("Valor de I : "+ i);

		}
		

	}

}
